/* THESE ARE THE MAIN TASKS AS DESCRIBED ON THE PROJECT DESCRIPTION---------------------- */
//this makes the robot wander
task Wander()
{
	//add in a loop for timer to make the robot stop program after a certain amount of time (nothing is found)
	ClearTimer(T1);
	while(time10[T1] < 12000);//2 minutes
	{
		switch(random(8)){ //goes in one of the 8 directions
			case 0:
				Forward(random(50)+25);
			break;
			case 1:
				Left(random(50)+25);
			break;
			case 2:
				Right(random(50)+25);
			break;
			case 3:
				Backwards(random(50)+25);
			break;
			case 4:
				Forward(random(50)+25);
				Left(random(50)+25);
			break;
			case 5:
				Forward(random(50)+25);
				Right(random(50)+25);
			break;
			case 6:
				Backwards(random(50)+25);
				Left(random(50)+25);
			break;
			case 7:
				Backwards(random(50)+25);
				Right(random(50)+25);
			break;
			default:
			nxtDisplayStringAt(0, 31, "error with random number generator");
			break;

	}
	}

	//enable(pid);
	//Forward();
}

//this makes the robot move towards the egg
//useing the ULTRASONIC_PORT to close the distance from robot to egg
task MoveTowardsEgg()
{
	int distance = 10;
	while(SensorValue(ultrasonicSensor) > distance ){
		Forward();
	}
}
